import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Routes, Route } from 'react-router-dom';
import Login from './Login';
import Register from './Register';
import '../style/App.css';

const App = () => {
  const [APIHealth, setAPIHealth] = useState('');
  const [authToken, setAuthToken] = useState(window.localStorage.getItem('st-token'));
  const DB_NAME = 'Git-Baked';
  const DB_URL =
process.env.DATABASE_URL || `postgres://localhost:5432/${DB_NAME}`;

  // useEffect(() => {
    // follow this pattern inside your useEffect calls:
    // first, create an async function that will wrap your axios service adapter
    // invoke the adapter, await the response, and set the data
    // const getAPIStatus = async () => {
    //   const { healthy } = await getAPIHealth();
    //   setAPIHealth(healthy ? 'api is up! :D' : 'api is down :/');
    // };

    // second, after you've defined your getter above
    // invoke it immediately after its declaration, inside the useEffect callback
  //   getAPIStatus();
  // }, []);

  return (
    <div className="app-container">
      <header> 
      <h1>Welcome to Git Baked! Your online bakery.</h1>
      <p>API Status: {APIHealth}</p>
      <div className= 'links'>
      <Link className='link-one' to='/login'> Login </Link>
      <Link className='link-two'to='/register'> Register </Link>
      <Link to='/products'> Products </Link>
      </div>

      </header>

      <Routes>
        <Route path="/"/>
        <Route path="login" element={<Login />} />
        <Route path="register" element={<Register authToken={authToken} setAuthToken={setAuthToken} DB_URL={DB_URL} />} />
        <Route path="products" element={<h2> Products </h2>} />
      </Routes>
    </div>
  );
}


export default App;
